// –ì–ª–æ–±–∞–ª—å–Ω—ã–µ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ
let currentCategory = 'all';
let searchQuery = '';

// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è
document.addEventListener('DOMContentLoaded', function() {
    renderFunctions();
    setupEventListeners();
});

// –†–µ–Ω–¥–µ—Ä —Ñ—É–Ω–∫—Ü–∏–π
function renderFunctions() {
    const grid = document.getElementById('functionsGrid');
    console.log('functionsData length:', functionsData.length);
    console.log('functionsData:', functionsData);
    
    const filteredFunctions = functionsData.filter(func => {
        const matchesCategory = currentCategory === 'all' || func.category === currentCategory;
        const matchesSearch = !searchQuery || 
            func.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            func.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
            func.speech.toLowerCase().includes(searchQuery.toLowerCase());
        return matchesCategory && matchesSearch;
    });
    
    console.log('filteredFunctions length:', filteredFunctions.length);
    console.log('filteredFunctions:', filteredFunctions);

    grid.innerHTML = filteredFunctions.map((func, index) => {
        const icon = func.icon || functionIcons[func.title] || "üìã";
        
        return `
            <div class="function-card group cursor-pointer hover-lift" 
                 onclick="showFunctionDetail('${func.title.replace(/'/g, "\\'")}')"
                 data-category="${func.category}">
                
                <!-- –ó–∞–≥–æ–ª–æ–≤–æ–∫ -->
                <div class="function-title">
                    ${highlightSearch(func.title)}
                </div>
                
                <!-- –û–ø–∏—Å–∞–Ω–∏–µ -->
                <div class="function-description">
                    ${highlightSearch(func.description.substring(0, 120))}${func.description.length > 120 ? '...' : ''}
                </div>
                
                <!-- –°—Å—ã–ª–∫–∞ –ü–æ–¥—Ä–æ–±–Ω–µ–µ -->
                <div class="function-link">
                    –ü–æ–¥—Ä–æ–±–Ω–µ–µ
                </div>
            </div>
        `;
    }).join('');

    // –û–±–Ω–æ–≤–ª—è–µ–º —Å—á–µ—Ç—á–∏–∫–∏ –ø–æ–∏—Å–∫–∞
    const searchCount = document.getElementById('searchCount');
    const searchCountMobile = document.getElementById('searchCountMobile');
    
    if (searchCount) {
        searchCount.textContent = `${filteredFunctions.length} –Ω–∞–π–¥–µ–Ω–æ`;
    }
    if (searchCountMobile) {
        searchCountMobile.textContent = `${filteredFunctions.length} –Ω–∞–π–¥–µ–Ω–æ`;
    }
}

// –ü–æ–∫–∞–∑–∞—Ç—å –¥–µ—Ç–∞–ª–∏ —Ñ—É–Ω–∫—Ü–∏–∏
function showFunctionDetail(title) {
    console.log('showFunctionDetail called with title:', title);
    
    const func = functionsData.find(f => f.title === title);
    if (!func) {
        console.error('Function not found:', title);
        return;
    }
    
    console.log('Found function:', func);
    
    const modal = document.getElementById('functionModal');
    const modalTitle = document.getElementById('modalTitle');
    const modalDescription = document.getElementById('modalDescription');
    const modalSpeech = document.getElementById('modalSpeech');
    
    if (!modal) {
        console.error('Modal not found: functionModal');
        return;
    }
    
    if (!modalTitle) {
        console.error('Modal title not found: modalTitle');
        return;
    }
    
    if (!modalDescription) {
        console.error('Modal description not found: modalDescription');
        return;
    }
    
    if (!modalSpeech) {
        console.error('Modal speech not found: modalSpeech');
        return;
    }
    
    console.log('All modal elements found, updating content...');
    
    modalTitle.textContent = func.title;
    modalDescription.innerHTML = func.description;
    modalSpeech.textContent = func.speech;
    
    console.log('Showing modal...');
    modal.classList.remove('hidden');
    
    console.log('Modal should be visible now');
}

// –ù–∞—Å—Ç—Ä–æ–π–∫–∞ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–æ–≤ —Å–æ–±—ã—Ç–∏–π
function setupEventListeners() {
    // –ü–æ–∏—Å–∫ (–¥–µ—Å–∫—Ç–æ–ø)
    const searchInput = document.getElementById('searchInput');
    if (searchInput) {
        searchInput.addEventListener('input', function(e) {
            searchQuery = e.target.value;
            renderFunctions();
        });
    }
    
    // –ü–æ–∏—Å–∫ (–º–æ–±–∏–ª—å–Ω—ã–π)
    const searchInputMobile = document.getElementById('searchInputMobile');
    if (searchInputMobile) {
        searchInputMobile.addEventListener('input', function(e) {
            searchQuery = e.target.value;
            // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ–º —Å –¥–µ—Å–∫—Ç–æ–ø–Ω—ã–º –ø–æ–ª–µ–º
            if (searchInput) {
                searchInput.value = e.target.value;
            }
            renderFunctions();
        });
    }
    
    // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è –ø–æ–ª–µ–π –ø–æ–∏—Å–∫–∞
    if (searchInput && searchInputMobile) {
        searchInput.addEventListener('input', function(e) {
            searchInputMobile.value = e.target.value;
        });
        
        searchInputMobile.addEventListener('input', function(e) {
            searchInput.value = e.target.value;
        });
    }

    // –¢–∞–±—ã
    document.querySelectorAll('.tab-filter').forEach(btn => {
        btn.addEventListener('click', function() {
            document.querySelectorAll('.tab-filter').forEach(b => {
                b.classList.remove('active');
            });
            this.classList.add('active');
            currentCategory = this.dataset.category;
            renderFunctions();
        });
    });

    // –ú–æ–¥–∞–ª—å–Ω—ã–µ –æ–∫–Ω–∞
    document.getElementById('closeFunctionModal').addEventListener('click', function() {
        document.getElementById('functionModal').classList.add('hidden');
    });

    // –ó–∞–∫—Ä—ã—Ç–∏–µ –º–æ–¥–∞–ª—å–Ω—ã—Ö –æ–∫–æ–Ω –ø–æ –∫–ª–∏–∫—É –≤–Ω–µ –∏—Ö
    document.getElementById('functionModal').addEventListener('click', function(e) {
        if (e.target === this) {
            this.classList.add('hidden');
        }
    });
}



// –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏
function highlightSearch(text) {
    if (!searchQuery) return text;
    const regex = new RegExp(`(${searchQuery})`, 'gi');
    return text.replace(regex, '<mark class="bg-yellow-200 px-1 rounded">$1</mark>');
}

function getCategoryName(category) {
    const names = {
        basic: '–û—Å–Ω–æ–≤–Ω—ã–µ',
        financial: '–§–∏–Ω–∞–Ω—Å–æ–≤—ã–µ',
        legal: '–Æ—Ä–∏–¥–∏—á–µ—Å–∫–∏–µ',
        analytics: '–ê–Ω–∞–ª–∏—Ç–∏–∫–∞'
    };
    return names[category] || '–î—Ä—É–≥–∏–µ';
}

// –ö–æ–ø–∏—Ä–æ–≤–∞–Ω–∏–µ –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞
function copyToClipboard(text) {
    navigator.clipboard.writeText(text).then(() => {
        showNotification('–°–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–æ –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞!');
    }).catch(err => {
        console.error('–û—à–∏–±–∫–∞ –∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∏—è:', err);
        showNotification('–û—à–∏–±–∫–∞ –∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∏—è', 'error');
    });
}

// –ü–æ–∫–∞–∑–∞—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ
function showNotification(message, type = 'success') {
    const notification = document.createElement('div');
    notification.className = `notification ${type === 'success' ? 'bg-green-500' : 'bg-red-500'} hide`;
    notification.innerHTML = `
        <div class="flex items-center space-x-3">
            <span class="text-2xl">${type === 'success' ? '‚úÖ' : '‚ùå'}</span>
            <span class="font-semibold">${message}</span>
        </div>
    `;
    
    document.body.appendChild(notification);
    
    // –ü–æ–∫–∞–∑–∞—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ
    setTimeout(() => notification.classList.remove('hide'), 100);
    
    // –°–∫—Ä—ã—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã
    setTimeout(() => {
        notification.classList.add('hide');
        setTimeout(() => document.body.removeChild(notification), 500);
    }, 3000);
} 
